import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent {

  products = [
    'digifitindia ISA',
    'digifitindia Mentorship',
    'digifitindia Profile Building',
    'digifitindia College Counsellors',
    'digifitindia Finances',
    'digifitindia Abroad Admission'
  ];

  students = [
    {
      name: 'Mentorship',
      desc: 'Access guidance from top industry professionals and structured mentorship programs to expedite career growth.',
      icon: 'partner_exchange'
    },
    {
      name: 'Networking Opportunity',
      desc: 'Forge connections with leading professionals and organizations globally, fostering collaborative growth and expansive networks.',
      icon: 'diversity_2'
    },
    {
      name: 'Global Education',
      desc: 'Seize the potential of merit-based scholarships to access diverse educational opportunities worldwide, broadening horizons and experiences.',
      icon: 'travel_explore'
    },
    {
      name: 'Access To Growth Capital',
      desc: 'Empower your aspirations with merit-based funding, opening doors to global opportunities and expansive growth potential.',
      icon: 'school'
    }
  ];

  mentors = [
    {
      name: 'Influence Over Talent',
      desc: 'Harness your expertise to empower talent growth, foster team building, and make a meaningful impact through valuable connections.',
      icon: 'stat_2'
    },
    {
      name: 'Massive Social Impact',
      desc: 'Seize the opportunity to drive purposeful change through investment, leading to significant social impact.',
      icon: 'diversity_3'
    },
    {
      name: 'Access to Community',
      desc: 'Join a vibrant community where you can connect with fellow experts, peers, and emerging talents, building a robust network for collaboration and support.',
      icon: 'diversity_1'
    },
    {
      name: 'Higher Returns',
      desc: 'Experience anticipated XIRR returns of up to 100%, surpassing all other investment options, rewarding your mentorship with substantial financial gains.',
      icon: 'redeem'
    }
  ]

}
